mapper.select:
SELECT 

FROM mms_assess_suggestion a

WHERE a.id = #{id}

SELECT 

FROM mms_assess_suggestion a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.suggestion_type = #{suggestionType}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mms_assess_suggestion a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mms_assess_suggestion SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
