SELECT 

FROM mms_comprehensive_product a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.sample_time = #{sampleTime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mms_comprehensive_product a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
