mapper.select:
SELECT 

FROM mms_policies_regulations a

WHERE a.id = #{id}

SELECT 

FROM mms_policies_regulations a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.file_name = #{fileName}


		AND a.document_number = #{documentNumber}


		AND a.file_type = #{fileType}


		AND a.file_timeliness = #{fileTimeliness}




		ORDER BY ${page.orderBy}


		ORDER BY a.sequence ASC ,a.update_date DESC

SELECT 

FROM mms_policies_regulations a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mms_policies_regulations SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
