mapper.select:
SELECT 

FROM mms_product_flow_number a

WHERE a.id = #{id}

SELECT 

FROM mms_product_flow_number a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mms_product_flow_number a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mms_product_flow_number SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
